import react,{useState} from "react";
import "./ExpenseForm.css";
export default function ExpenseForm() {

    // different state for different fields
    // const [enteredTitle,setEnteredTitle] = useState('');
    // const [enteredAmount,setEnteredAmount] = useState('');
    // const [enteredDate,setEnteredDate] = useState('');

    //using single state for all field
    const [enteredValue,setEnteredValue] = useState({
        enteredTitle:'',
        enteredAmount:'',
        enteredDate:''
    })

    const titleHandler = (event) => {
        // using different state for different fields
        // setEnteredTitle(event.target.value)

        //using single state
        // setEnteredValue({
        //     ...enteredValue,
        //     enteredTitle:event.target.value
        // })

        //more professional and correct way of using single state
        setEnteredValue((previousState)=>{
            return {...previousState, enteredTitle:event.target.value}
        });

    }
    const amountHandler = (event) => {
        // using different state for different fields
        // setEnteredTitle(event.target.value)

        //using single state
        // setEnteredValue({
        //     ...enteredValue,
        //     enteredAmount:event.target.value
        // })

        //more professional and correct way of using single state
        setEnteredValue((previousState)=>{
            //console.log(enteredTitle);
            return {...previousState, enteredAmount:event.target.value}
        });
    }
    const dateHandler = (event) => {
        // using different state for different fields
        // setEnteredTitle(event.target.value)

        //using single state
        // setEnteredValue({
        //     ...enteredValue,
        //     enteredDate:event.target.value
        // })

        //more professional and correct way of using single state
        setEnteredValue((previousState)=>{
            return {...previousState, enteredDate:event.target.value}
        });
    }



  return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense__control">
            <label>Title</label>
            <input type="text" onChange={titleHandler}></input>
        </div>
        <div className="new-expense__control">
            <label>Amount</label>
            <input type="number" min="0.01" step="0.01" onChange={amountHandler}></input>
        </div>
        <div className="new-expense__control">
            <label>Date</label>
            <input type="date" min='2019-01-01' max='2024-12-31' onChange={dateHandler}></input>
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
}
